(function (win, doc) {
    var head = doc.getElementsByTagName("head")[0], loadscript = function (scripts, callback) {
        var src = scripts.shift(), script = doc.createElement("script");
        script.type = "text/javascript";
        script.async = false;
        script.onload = script.onreadystatechange = function () {
            if (!this.readyState || /loaded|complete/.test(this.readyState)) {
                script.onload = script.onreadystatechange = null;
                if (scripts.length > 0) {
                    load_script(scripts, callback)
                } else {
                    callback && callback();
                }
            }
        };
        script.src = src;
        head.appendChild(script)
    };
    win.loadscript = loadscript;
})(window, document);